import './App.css';
import React, { useState, useEffect } from 'react';
import logo from './notepal_logo.png'
import Autocomplete from '@mui/material/Autocomplete';
import TextField from '@mui/material/TextField';
import Select from 'react-select'

function App() {
  const [loaded, setLoaded] = useState(false);
  const [classes, setClasses] = useState([]);
  const [placeholder, setPlaceholder] = useState();
  useEffect(() => {
    fetch('http://localhost:4545/searchFormat')
    .then((response) => response.json())
    .then((data) => {
    console.log(data);
    setLoaded(true);
    setClasses(data);
  })
  .catch(error => console.log(error))
  }, [])
  return (
    <div className="app">
      <div className="container">
        <div className="imageContainer">
          <img className="logo" src={logo} alt='NotePal Logo'/>
        </div>
        <div className='motto'>
        <b>NotePal</b> is love. <b>NotePal</b> is life.
        </div>
        <div className='inputContainer'>
          {/* <input className='input' placeholder='Oh what the future holds...'></input> 
           */}
          <Autocomplete
            sx={{
              '& .MuiOutlinedInput-root .MuiAutocomplete-input': {
                padding: 0,
            },
              "& .MuiOutlinedInput-root .MuiOutlinedInput-notchedOutline":{
                border: "0",
            }
            }}
            className='input'
            freeSolo
            options={classes}
            onChange={setPlaceholder(data[Math.floor(Math.random(classes.length))]['label'])}
            renderInput={(params) => <TextField {...params} placeholder={placeholder}/>}
          />

        </div>
      </div>
    </div>
  );
}

export default App;
